import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Form, Button } from "react-bootstrap";
import axios from "axios";

const AddProperty = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    mobile: "",
    email: "",
    role: "", // Add role to the form data
    images: [],
  });

  const [roles, setRoles] = useState([]); // State for storing available roles
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState(""); // For success confirmation

  // Fetch roles from the backend
  useEffect(() => {
    const fetchRoles = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8080/auth/role-list"
        ); // Replace with your endpoint for fetching roles
        if (response.data.success) {
          setRoles(response.data.data); // Set roles from API response
        } else {
          console.error("Failed to fetch roles", response.data.message);
        }
      } catch (error) {
        console.error("Error fetching roles", error);
      }
    };
    fetchRoles();
  }, []);

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      images: Array.from(e.target.files), // Convert FileList to Array
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append("name", formData.name);
    data.append("mobile", formData.mobile);
    data.append("email", formData.email);
    data.append("role", formData.role); // Append role

    // Append images to FormData
    formData.images.forEach((image) => {
      data.append("images", image);
    });

    try {
      const response = await axios.post(
        "http://localhost:8080/auth/add-userrole",
        data,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setSuccessMessage("User Role added successfully!"); // Show success message

      // Clear the form after successful submission
      setFormData({
        name: "",
        mobile: "",
        email: "",
        role: "",
        images: [],
      });

      setTimeout(() => setSuccessMessage(""), 3000); // Clear message after 3 seconds
    } catch (error) {
      setError("Error adding user role");
      console.error("Error adding user role", error);
    }
  };

  const handleBackClick = () => {
    navigate("/"); // Navigate to home page
  };
  console.log();
  return (
    <div>
      <Button
        variant="secondary"
        onClick={handleBackClick}
        style={{ marginBottom: "20px" }}
      >
        Back
      </Button>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
            className="form-control-sm"
            style={{ width: "50%" }}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Mobile</Form.Label>
          <Form.Control
            type="number"
            value={formData.mobile}
            onChange={(e) =>
              setFormData({ ...formData, mobile: e.target.value })
            }
            required
            className="form-control-sm"
            style={{ width: "50%" }}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            value={formData.email}
            onChange={(e) =>
              setFormData({ ...formData, email: e.target.value })
            }
            required
            className="form-control-sm"
            style={{ width: "50%" }}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Role</Form.Label>
          <Form.Control
            as="select"
            value={formData.role}
            onChange={(e) => setFormData({ ...formData, role: e.target.value })}
            required
            className="form-control-sm"
            style={{ width: "50%" }}
          >
            <option value="">Select a Role</option>
            {roles.map((role) => (
              <option key={role.id} value={role.id}>
                {role.label}
              </option>
            ))}
          </Form.Control>
        </Form.Group>

        <Form.Group>
          <Form.Label>Upload Images</Form.Label>
          <Form.Control
            type="file"
            multiple
            onChange={handleFileChange}
            className="form-control-sm"
            style={{ width: "50%" }}
          />
        </Form.Group>

        <Button
          type="submit"
          style={{
            marginTop: "10px",
          }}
        >
          Add User Role
        </Button>

        {error && <div className="alert alert-danger mt-3">{error}</div>}
        {successMessage && (
          <div className="alert alert-success mt-3">{successMessage}</div>
        )}
      </Form>
    </div>
  );
};

export default AddProperty;
