import React, { useState, useEffect } from "react";
import "../App.css";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const UpdateRolePage = () => {
  //   const { roleId } = useParams(); // Get role ID from the URL
  const navigate = useNavigate();
  const [roleName, setRoleName] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  // Fetch the current role details on component load
  useEffect(() => {
    const fetchRoleDetails = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/auth/role-details/${roleId}`
        );
        if (response.data.success) {
          setRoleName(response.data.data.name); // Set the current role name
        } else {
          console.error("Failed to fetch role details:", response.data.message);
          setErrorMessage("Failed to fetch role details.");
        }
      } catch (error) {
        console.error("Error fetching role details:", error);
        setErrorMessage("Error fetching role details.");
      }
    };

    fetchRoleDetails();
  }, [roleId]);

  const handleUpdate = async () => {
    if (roleName.trim()) {
      try {
        // Send the updated role name to the API
        await axios.put(
          `http://localhost:8080/auth/update-role/${roleId}`,
          { name: roleName },
          {
            headers: {
              "Content-Type": "application/json",
            },
          }
        );

        // Show success message and navigate back after 3 seconds
        setSuccessMessage(`Role "${roleName}" updated successfully!`);
        setTimeout(() => navigate("/roles"), 3000); // Redirect to the roles list
      } catch (error) {
        console.error("Error updating role:", error);
        setErrorMessage("Failed to update the role. Please try again.");
      }
    } else {
      alert("Role Name is required!");
    }
  };

  const handleCancel = () => {
    navigate("/roles"); // Redirect to the roles list
  };

  return (
    <div className="update-role-container">
      <header className="update-role-header">
        <h2>Update Role</h2>
      </header>
      <div className="form-container">
        <label htmlFor="roleName" className="form-label">
          Role Name
        </label>
        <input
          type="text"
          id="roleName"
          className="form-input"
          value={roleName}
          onChange={(e) => setRoleName(e.target.value)}
          placeholder="Enter role name"
        />
        <div className="form-buttons">
          <button className="cancel-btn" onClick={handleCancel}>
            Cancel
          </button>
          <button className="save-btn" onClick={handleUpdate}>
            Update
          </button>
        </div>
        {successMessage && (
          <div className="alert alert-success mt-3">{successMessage}</div>
        )}
        {errorMessage && (
          <div className="alert alert-danger mt-3">{errorMessage}</div>
        )}
      </div>
    </div>
  );
};

export default UpdateRolePage;
