import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../App.css";
import axios from "axios";
import { Button } from "react-bootstrap";

const RolesPage = () => {
  const [roles, setRoles] = useState([]); // State to store roles data
  const [searchTerm, setSearchTerm] = useState(""); // State for search functionality

  // Fetch roles data from the API
  useEffect(() => {
    fetchRoles();
  }, []);

  const fetchRoles = async () => {
    try {
      const response = await axios.get("http://localhost:8080/auth/list-role");
      setRoles(response?.data?.data || []);
    } catch (error) {
      console.error("Error fetching roles:", error);
    }
  };

  // Filter roles based on the search term
  const filteredRoles = roles.filter((role) =>
    role.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="roles-container">
      <header className="roles-header">
        <h2>Roles</h2>
        <div className="search-and-add">
          <input
            type="text"
            placeholder="Search..."
            className="search-bar"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <Link to="/add-role" className="add-new-btn">
            Add New
          </Link>
        </div>
      </header>
      <table className="roles-table">
        <thead>
          <tr>
            <th>Sr. No.</th>
            <th>Role Name</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredRoles.length > 0 ? (
            filteredRoles.map((role, index) => (
              <tr key={role._id || index}>
                <td>{index + 1}</td>
                <td>{role.name}</td>
                <td
                  className={
                    role.isActive ? "status-active" : "status-inactive"
                  }
                >
                  {role.isActive ? "Active" : "Inactive"}
                </td>
                <td className="actions">
                  <Link to={`/editrole/${role._id}`}>
                    <Button variant="primary" className="edit-btn">
                      Edit
                    </Button>
                  </Link>
                  <Button
                    variant="danger"
                    className="delete-btn"
                    onClick={() => {
                      // Add delete functionality here
                      alert(`Delete role with ID: ${role._id}`);
                    }}
                  >
                    Delete
                  </Button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={4} align="center">
                No roles available.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default RolesPage;
