import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Form, Button } from "react-bootstrap";
import axios from "axios";

const AddProperty = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    location: "",
    price: "",
    description: "",
    propertyType: "residential",
    images: [],
  });

  const [uploadedImages, setUploadedImages] = useState([]); // To store uploaded image URLs
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState(""); // For success confirmation

  const handleFileChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    const newImages = selectedFiles.map((file) => URL.createObjectURL(file)); // Create object URLs for immediate preview
    setFormData({ ...formData, images: selectedFiles }); // Store the file objects
    setUploadedImages((prevImages) => [...prevImages, ...newImages]); // Add new images to the preview
  };

  const handleRemoveImage = (index) => {
    // Remove image from preview
    const newUploadedImages = uploadedImages.filter((_, i) => i !== index);
    const newImages = formData.images.filter((_, i) => i !== index); // Remove corresponding file object
    setUploadedImages(newUploadedImages);
    setFormData({ ...formData, images: newImages });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const form = new FormData();
    form.append("name", formData.name);
    form.append("location", formData.location);
    form.append("price", formData.price);
    form.append("description", formData.description);
    form.append("propertyType", formData.propertyType);
    formData.images.forEach((image) => form.append("images", image));

    try {
      const response = await axios.post(
        "http://localhost:5000/api/properties",
        form,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      // Set the uploaded image URLs returned from the server
      setUploadedImages(response.data.images);
      setSuccessMessage("Property added successfully!"); // Show success message

      // Clear the form after successful submission
      setFormData({
        name: "",
        location: "",
        price: "",
        description: "",
        propertyType: "residential",
        images: [],
      });
      setUploadedImages([]); // Reset uploaded images
      setTimeout(() => setSuccessMessage(""), 3000); // Clear message after 3 seconds
    } catch (error) {
      setError("Error uploading property");
      console.error("Error uploading property", error);
    }
  };
  const handleBackClick = () => {
    navigate("/"); // Navigate to home page
  };
  return (
    <div>
      <Button
        variant="secondary"
        onClick={handleBackClick}
        style={{ marginBottom: "20px" }}
      >
        Back
      </Button>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
            className="form-control-sm"
            style={{ width: "50%" }}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>mobile</Form.Label>
          <Form.Control
            type="number"
            value={formData.price}
            onChange={(e) =>
              setFormData({ ...formData, price: e.target.value })
            }
            required
            className="form-control-sm"
            style={{ width: "50%" }}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>email</Form.Label>
          <Form.Control
            as="textarea"
            value={formData.description}
            onChange={(e) =>
              setFormData({ ...formData, description: e.target.value })
            }
            className="form-control-sm"
            style={{ width: "50%" }}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Images</Form.Label>
          <Form.Control
            type="file"
            multiple
            onChange={handleFileChange}
            className="form-control-sm"
            style={{ width: "30%" }}
          />
        </Form.Group>

        <Button
          type="submit"
          style={{
            marginTop: "10px",
          }}
        >
          Add Property
        </Button>

        {error && <div className="alert alert-danger mt-3">{error}</div>}
        {successMessage && (
          <div className="alert alert-success mt-3">{successMessage}</div>
        )}

        {/* Display uploaded images immediately after selection */}
        {uploadedImages.length > 0 && (
          <div className="mt-3">
            <h4>Uploaded Images:</h4>
            <div
              className="image-preview"
              style={{ display: "flex", flexWrap: "wrap" }}
            >
              {uploadedImages.map((imageUrl, index) => (
                <div
                  key={index}
                  style={{ position: "relative", margin: "10px" }}
                >
                  <img
                    src={imageUrl}
                    alt={`uploaded ${index}`}
                    style={{
                      width: "100px",
                      height: "100px",
                      objectFit: "cover",
                      margin: "10px",
                    }}
                  />
                  <button
                    onClick={() => handleRemoveImage(index)}
                    style={{
                      position: "absolute",
                      top: 0,
                      right: 0,
                      background: "transparent",
                      border: "none",
                      cursor: "pointer",
                      color: "red",
                      fontSize: "16px",
                    }}
                  >
                    &#10006; {/* Cross icon */}
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}
      </Form>
    </div>
  );
};

export default AddProperty;
