import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Form, Button } from "react-bootstrap";
import axios from "axios";

const AddProperty = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    mobile: "",
    email: "",
  });

  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState(""); // For success confirmation

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "http://localhost:8080/auth/add-userrole",
        formData
      );

      setSuccessMessage("User Role added successfully!"); // Show success message

      // Clear the form after successful submission
      setFormData({
        name: "",
        mobile: "",
        email: "",
      });

      setTimeout(() => setSuccessMessage(""), 3000); // Clear message after 3 seconds
    } catch (error) {
      setError("Error adding user role");
      console.error("Error adding user role", error);
    }
  };

  const handleBackClick = () => {
    navigate("/"); // Navigate to home page
  };

  return (
    <div>
      <Button
        variant="secondary"
        onClick={handleBackClick}
        style={{ marginBottom: "20px" }}
      >
        Back
      </Button>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
            className="form-control-sm"
            style={{ width: "50%" }}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Mobile</Form.Label>
          <Form.Control
            type="number"
            value={formData.mobile}
            onChange={(e) =>
              setFormData({ ...formData, mobile: e.target.value })
            }
            required
            className="form-control-sm"
            style={{ width: "50%" }}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            value={formData.email}
            onChange={(e) =>
              setFormData({ ...formData, email: e.target.value })
            }
            required
            className="form-control-sm"
            style={{ width: "50%" }}
          />
        </Form.Group>

        <Button
          type="submit"
          style={{
            marginTop: "10px",
          }}
        >
          Add User Role
        </Button>

        {error && <div className="alert alert-danger mt-3">{error}</div>}
        {successMessage && (
          <div className="alert alert-success mt-3">{successMessage}</div>
        )}
      </Form>
    </div>
  );
};

export default AddProperty;
